import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postCharacters, getOccupations } from "../actions/index";
import { useDispatch, useSelector } from "react-redux";

export default function CharacterCreated() {
  const dispatch = useDispatch();
  const history = useHistory();
  const occupations = useSelector((state) => state.occupations);

  const [input, setInput] = useState({
    name: "",
    nickname: "",
    birthday: "",
    status: "",
    occupations: [],
  });

  function handleChange(e) {
    setInput({
      ...input,
      [e.target.name]: e.targe.value,
    });
    console.log(input);
  }


  function handleCheck(e){
      if(e.target.checked){
          setInput({
              ...input,
              status:e.target.value
          })
      }
  }

  useEffect(() => {
    dispatch(getOccupations());
  }, [dispatch]);

  return (
    <div>
      <Link to="/home">
        <button>Volver</button>
      </Link>
      <h1>Crear tu personaje</h1>
      <form action="">
        <div>
          <label>Nombre:</label>
          <input
            type="text"
            value={input.name}
            name="name"
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Birthday:</label>
          <input
            type="text"
            value={input.nickname}
            name="nickname"
            onChange={handleChange}
          />
        </div>
        <div>
          <label htmlFor="">Imagen:</label>
          <input
            type="text"
            value={input.image}
            name="image"
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Birthday:</label>
          <input
            type="text"
            value={input.birthday}
            name="birthday"
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Status</label>
          <label>
            <input type="checkbox" name="Alive" value="Alive" />
            Alive
          </label>
          <label>
            <input type="checkbox" name="Decease" value="Decease" />
            Decease
          </label>
          <label>
            <input type="checkbox" name="Unknown" value="Unknown" />
            Unknown
          </label>
        </div>
        <select>
          {occupations.map((occ) => (
            <option value={occ.name}>{occ.name}</option>
          ))}
        </select>
        <button type="submit">Crear personaje</button>
      </form>
    </div>
  );
}
