const initialState = {
  characters: [],
  allCharactes: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    case "GET_CHARACTERS":
      return {
        ...state,
        characters: action.payload,
        allCharactes: action.payload,
      };
    case "FILTER_BY_STATUS":
      const allCharactes = state.allCharactes;
      const statusFiiltered =
        action.payload === "All"
          ? allCharactes
          : allCharactes.filter((el) => el.status === action.payload);

      return {
        ...state,
        characters: statusFiiltered,
      };
    case "FILTER_CREATED":
      const allCharactes2 = state.allCharactes;
      const createdFilter =
        action.payload === "created"
          ? allCharactes2.filter((el) => el.createdInDb)
          : allCharactes2.filter((el) => !el.createdInDb);
      return {
        ...state,
        characters: action.payload === "All" ? state.allCharactes: createdInDb,
      };
    default:
      return state;
  }
}

export default rootReducer;
