import React, { Fragment, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getCharacters } from "../actions";
import { Link } from "react-router-dom";
import Card from "./Card";

export default function Home() {

  /* Hooks */
  const dispatch = useDispatch();
  const allCharacters = useSelector((state) => state.characters);

  useEffect(() => {
    dispatch(getCharacters());
  }, [dispatch]);

  /* Esta función es la que genera la acción del botón de abajo -> */
  function handleClick(e) {
    e.preventDefault();
    dispatch(getCharacters());
  }

  return (
    <div>
      <Link to="/character">Crear Personaje</Link>
      <h1>EN QUE TALLITA LO BUSCABA... MOR</h1>
     {/* de este boton -> */} <button
        onClick={(e) => {
          handleClick(e);
        }}
      >
        Volver a cargar todos los personajes
      </button>
      <div>
        <select>
          <option value="asc">Ascendente</option>
          <option value="des">Descendente</option>
        </select>
        <select>
          <option value="All">Todos</option>
          <option value="Alive">Vivo</option>
          <option value="Deceased">Muerto</option>
          <option value="Unknown">Desconocido</option>
          <option value="Presumed dead">Probablemente</option>
        </select>
        <select>
          <option value="All">Todos</option>
          <option value="created">Creados</option>
          <option value="api">Existentes</option>
        </select>
        <div className='tarjetas'>
        {
        allCharacters?.map((el) => {
          return (
            <Fragment>
              <Link to={"/home/" + el.id}>
                <Card 
                  name={el.name}
                  image={el.img}
                  nickname={el.name}
                  key={el.id}
                />
              </Link>
            </Fragment>
          );
        })}
        </div>
      </div>
    </div>
  );
}
